package com.codility.trainning.CodeKata;

import junit.framework.TestCase;
import org.junit.Test;

/**
 * Created by rafael.alves on 29/01/2016.
 */
public class CodeKataTest extends TestCase {
    @Test
    public void testeBasico() {
	PosicaoTO pTO = new PosicaoTO();
	PosicaoTO pTOEsperado = new PosicaoTO();
	CodeKata ck = new CodeKata();
	pTO = ck.encontrar("BYX", "XYBABCFBYXELMBYXYYYYYYYJKJGDKGSHGDHGDHSGDHSGDHSGDHJSGHJDGXYBSHJGDHJDSGHJBYJX");
	pTOEsperado.addPosicaoDireta(7);
	pTOEsperado.addPosicaoDireta(13);
	pTOEsperado.addPosicaoReversa(0);
	pTOEsperado.addPosicaoReversa(57);
	assertEquals(pTOEsperado.getListaPosicoesDireta(), pTO.getListaPosicoesDireta());
	assertEquals(pTOEsperado.getListaPosicoesReversa(), pTO.getListaPosicoesReversa());
    }

    @Test
    public void testeBasicoOnlyWord() {
	PosicaoTO pTO = new PosicaoTO();
	PosicaoTO pTOEsperado = new PosicaoTO();
	CodeKata ck = new CodeKata();
	pTO = ck.encontrar("A", "AAABBBBBXXX");
	pTOEsperado.addPosicaoDireta(0);
	pTOEsperado.addPosicaoDireta(1);
	pTOEsperado.addPosicaoDireta(2);
	pTOEsperado.addPosicaoReversa(0);
	pTOEsperado.addPosicaoReversa(1);
	pTOEsperado.addPosicaoReversa(2);
	assertEquals(pTOEsperado.getListaPosicoesDireta(), pTO.getListaPosicoesDireta());
	assertEquals(pTOEsperado.getListaPosicoesReversa(), pTO.getListaPosicoesReversa());
    }

    @Test
    public void testeBasicoSameBiggerString() {
	PosicaoTO pTO = new PosicaoTO();
	PosicaoTO pTOEsperado = new PosicaoTO();
	CodeKata ck = new CodeKata();
	pTO = ck.encontrar(
			"",
			"");
	pTOEsperado.addPosicaoDireta(0);
	assertEquals(pTOEsperado.getListaPosicoesDireta(), pTO.getListaPosicoesDireta());
	assertEquals(pTOEsperado.getListaPosicoesReversa(), pTO.getListaPosicoesReversa());
    }

    @Test
    public void testeBasicoSameSmallString() {
	PosicaoTO pTO = new PosicaoTO();
	PosicaoTO pTOEsperado = new PosicaoTO();
	CodeKata ck = new CodeKata();
	pTO = ck.encontrar("A", "A");
	pTOEsperado.addPosicaoDireta(0);
	pTOEsperado.addPosicaoReversa(0);
	assertEquals(pTOEsperado.getListaPosicoesDireta(), pTO.getListaPosicoesDireta());
	assertEquals(pTOEsperado.getListaPosicoesReversa(), pTO.getListaPosicoesReversa());
    }

    @Test
    public void testeBasicoVazio() {
	PosicaoTO pTO = new PosicaoTO();
	PosicaoTO pTOEsperado = new PosicaoTO();
	CodeKata ck = new CodeKata();
	pTO = ck.encontrar("", "");
	assertEquals(pTOEsperado.getListaPosicoesDireta(), pTO.getListaPosicoesDireta());
	assertEquals(pTOEsperado.getListaPosicoesReversa(), pTO.getListaPosicoesReversa());
    }
}
